package JCS;

public class NestedIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=8,b=2,c=9;
		if(a>b && a>c)
		{
			System.out.println(a);
		}
		else
		{
			if(b>c && b>a)
			{
				System.out.println(b);
			}
			else
			{
				System.out.println(c);
			}
		}
			}
		

	}


