
public class Speedometer_program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
             for(int  i = 0 ;i<=9; i++)
             {
            	 for(int j=0;j<=9; j++)
            	 {
            		 for(int k=0;k<=9; k++)
            		 {
            			 for(int l=0;l<=9; l++)
            			 {
            				 System.out.println(i+" "+j+" "+k+" "+l);
            			 }
            		 }
            	 }
             }

	}

}
