
public class Testingprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=a++;
		
		System.out.println(a+"  "+b);
		
		int c=5;
		int d=++c;
		
		System.out.println(c+"  "+d);
		
		int e=5;
		int f=e--;
		
		System.out.println(e+"  "+f);
		
		int g=5;
		int h=--g;
		
		System.out.println(g+"  "+h);

	}
}

	